USE test;
DROP TABLE IF EXISTS book;
CREATE TABLE `book` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL,
  `description` varchar(255) DEFAULT NULL,
  `author` varchar(100) NOT NULL,
  `isbn` varchar(20) DEFAULT NULL,
  `printYear` int(11) NOT NULL,
  `readAlready` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;

INSERT INTO book (title, description, author, isbn, printYear) VALUES ('Кому на Руси жить хорошо','В книгу вошли самые известные произведения Н.А.Некрасова.Многоголосье русского народа - поэма-роман \"Кому на Руси жить хорошо\", в которой раскрывается картина многообразия жизни в России ключевого исторического периода - 1860-х годов.','Николай Некрасов','978-5-17-102593-9',2017),('Идиот','Главная идея... - писал Ф.М.Достоевский о своем романе \"Идиот\", - изобразить положительно-прекрасного человека. Труднее этого нет ничего на свете...','Федор Достоевский','978-5-389-04730-3',2016),('Преступление и наказание','Включенный во все школьные и вузовские программы, неоднократно экранизированный, роман Достоевского \"Преступление и наказание\" не перестал от этого быть одним из самых ярких и \"краеугольных\" произведений русской и мировой литературы.','Федор Достоевский','978-5-389-02279-9',2016),('Анна Каренина','\"Анна Каренина\" - лучший роман о женщине, написанный в XIX веке. По словам Ф.М.Достоевского, \"Анна Каренина\" поразила современников не только вседневностью содержания, но и огромной психологической разработкой души.','Лев Толстой','978-5-389-04935-2',2017),('Вишневый сад','В этот сборник вошли лучшие пьесы Чехова - \"Чайка\", \"Дядя Ваня\", \"Три сестры\" и \"Вишневый сад\". Главным достоинством всех чеховских пьес и его новаторством было то, что он впервые сделал предметом исследования мельчайшие детали быта и жизни людей.','Антон Чехов','978-5-17-097313-2',2010),('Вечера на хуторе близ Диканьки','\"Вечера на хуторе близ Диканьки\" - первый сборник повестей Н.В.Гоголя. Он был опубликован в 1831-1832 гг. и принес автору внезапную славу.','Николай Гоголь','978-5-389-05080-8',1995),('Гранатовый браслет','В настоящее издание замечательного русского писателя А.И.Куприна включены такие признанные шедевры, как \"Молох\", \"Олеся\", \"Поединок\", \"Гранатовый браслет\" и \"Суламифь\". Каждый из них - одно из лучших в мировой литературе произведений о любви.','Александр Куприн','978-5-389-05083-9',2007),('Война и мир','ВОЙНА И МИР Л.Н.Толстого — книга на все времена. Кажется, что она существовала всегда, настолько знакомым кажется текст, едва мы открываем первые страницы романа, настолько памятны многие его эпизоды','Лев Толстой','978-5-389-07123-0',2014),('Отцы и дети','\"Отцы и дети\" - самый философский из всех романов Тургенева, в котором писатель пытался осмыслить характер и направление \"новых людей\" - героев новой эпохи в общественной жизни России.','Иван Тургенев','978-5-389-04996-3',2012),('Герой нашего времени','\"Герой нашего времени\" (1840) по праву считается первым психологическим романом в русской литературе. В основе повествования — история жизни главного героя, Григория Печорина.','Михаил Лермонтов','978-5-17-092164-5',2015),('Братья Карамазовы','Последний, самый объемный и один из наиболее известных романов Ф.М.Достоевского обращает читателя к вневременным нравственно-философским вопросам о грехе, воздаянии, сострадании и милосердии.','Федор Достоевский','978-5-389-01463-3',2011),('Капитанская дочка','А.С.Пушкин вошел в русскую культуру не только как великий Поэт, но и как замечательный прозаик и драматург. В этот том включены наиболее значительные и известные прозаические произведения автора.','Александр Пушкин','978-5-389-04732-7',2012),('Евгений Онегин','Можно сказать, что весь XIX век прошел под знаком А.С.Пушкина. Но и сегодня, в XXI веке, уже тысячекратно повторенные школьными учебниками и хрестоматиями гениальные пушкинские строки не обесценились.','Александр Пушкин','978-5-389-04903-1',2016),('Дама с собачкой','В настоящем издании представлены избранные произведения Антона Павловича Чехова - писателя, чей талант оказал огромное влияние на развитие мировой литературы и театрального искусства. Чехов - замечательный стилист, тонкий психолог, мастер подтекста.','Антон Чехов','978-5-389-04733-4',2012),('Игрок','О великом русском писателе Ф.М.Достоевском уже полтора века спорят не только литературоведы, но и философы, культурологи, социологи, богословы.','Федор Достоевский','978-5-389-07127-8',2014),('Подросток','Желание денег и власти, мечта о превосходстве над миллионами \"обыкновенных\", проблема отцов и детей, богоборчество - вот опасности для русского подростка. Преодолимы ли они и что победит в итоге: бунт против людей и Бога или же любовь к живой жизни?..','Федор Достоевский','978-5-389-10473-0',2015),('Яма','Книга о жизни проституток вызвала скорее осуждение, чем сочувствие или понимание. Автора обвиняли в безнравственности и чрезмерном натурализме. Резко негативным было отношение к повести Л. Н. Толстого, который так и не смог дочитать ее до конца','Александр Куприн','978-5-389-11352-7',2016),('Воскресение','\"Воскресение\" - последний толстовский роман, в нем нашли отражение духовные и творческие искания последних лет жизни писателя. Начатый в 1889 году, роман был закончен и увидел свет только через 10 лет - в 1899 году.','Лев Толстой','978-5-389-05730-2',2013),('Горе от ума','Грибоедова называют \"гением одного произведения\" - но так ли это в действительности? В данном сборнике, помимо \"Горя от ума\", представлены и другие его пьесы, написанные как в одиночку, так и в соавторстве.','Александр Грибоедов','978-5-17-066980-6',2012),('Дворянское гнездо','В издании публикуется роман И.С.Тургенева \"Дворянское гнездо\". В нем с трагической остротой поставлен вопрос о праве человека на личное счастье.','Иван Тургенев','978-5-389-02702-2',2016),('Беседа пьяного с трезвым чертом','В сборнике объединены рассказы молодого Чехова, так или иначе связанные с темой пьянства. Взятые в совокупности, они представляют целую энциклопедию спиртных напитков, поводов и причин для их потребления, различных питейных обычаев и др.','Антон Чехов','978-5-389-03386-3',2012),('Обломов','Когда в 1859 году вышел в свет роман И.А.Гончарова \"Обломов\", читатели вряд ли догадывались, что держат в руках одну из \"краеугольных\" русских книг.','Иван Гончаров','978-5-389-10475-4',2015),('Гроза','Пьесы А.Н. Островского – целая эпоха в истории национального русского театра, важная веха на пути развития русской классической литературы и проверенный временем бестселлер.','Александр Островский','978-5-389-10618-5', 2015),('Палата №6','Современников поражало, насколько убедительно Чехов показывает различные душевные состояния — волнение, страх, припадок, душевную болезнь.','Антон Чехов','978-5-389-14427-9',2018),('Записки сумасшедшего','Повесть Н.В.Гоголя \"Записки сумасшедшего\" была опубликована в 1835 г. Внимание молодого писателя к теме безумия было вполне оправдано ее популярностью в литературе первой половины XIX века.','Николай Гоголь','978-5-389-01877-8',2014),('Что делать?','В настоящем издании публикуется роман Н. Г. Чернышевского \"Что делать?\" - одно из самых неоднозначных произведений русской литературы. Роман увидел свет в 1863 году и сразу же вызвал бурную полемику.','Николай Чернышевский','978-5-389-02082-5',2012),('Тарас Бульба','Мужество, непреклонная воля, недюжинная сила и любовь к отечеству - черты истинных героев в \"Тарасе Бульбе\".','Николай Гоголь','978-5-389-04899-7',2012),('Семейное счастие','В мировой литературе найдется не много писателей, которые с таким постоянством и бесстрашием, как Л.Н.Толстой, пытались дойти до корня, уловить скрытый смысл в стихийном потоке жизни и понять изменчивую  диалектику человеческой души.','Лев Толстой','978-5-389-13619-9',2017),('Драма на охоте','Кого-то из чеховских героев толкает на путь преступления бедность, кого-то - любовь. Растрата казенных денег, убийства, грабежи, мошенничество - обвиняемые предстают перед судом за различные преступления.','Антон Чехов','978-5-389-12320-5',2016),('Ревизор','Н.В.Гоголь - величайший русский писатель и драматург. Одной из вершин его творчества, самым известным его произведением по праву считается комедия \"Ревизор\".','Николай Гоголь','978-5-389-02530-1',2011);